mapper.update:
UPDATE TCBOARDALL
SET TC_TITLE= #{tc_title}, TC_CONTENT= #{tc_content}, TC_OPEN= #{tc_open}, TC_TAG= #{tc_tag}, TC_Q= #{tc_q}, TC_DC= #{tc_dc}
WHERE TC_NO= #{tc_no}

UPDATE TCBOARDALL
SET TC_VIEW = TC_VIEW + 1
WHERE TC_NO= #{room}

UPDATE TCBOARDALL
SET TC_LIKE = TC_LIKE + 1
WHERE TC_NO= #{room}

UPDATE TCBOARDALL
SET TC_JPG= #{tc_jpg}
WHERE TC_NO= #{tc_no}
